import axios from 'axios';
import React, { useEffect, useState } from 'react'
import './estilosComponentes/infoRestaurante.css'

const MenusInforRestaurantes = (datos) => {
    const [menusrestaurantes,setmenusrestaurantes]=useState([])
    async function getMenus(){
        alert("http://192.168.1.4:8080/restaurantes/menu?razonSocial="+datos.razonSocial)
        const respuesta=await axios.get("http://192.168.1.4:8080/restaurantes/menu?razonSocial="+datos.razonSocial)
        setmenusrestaurantes(respuesta.data)
    }
    useEffect(()=>{
        getMenus();
    },[]);
    function tipo(menu){
        if(menu.tipoMenu===1){
            return "Entrada"
        }else if(menu.tipoMenu===2){
            return "Plato Fuerte"
        }else if(menu.tipoMenu===3){
            return "Postres"
        }else{
            return "Bebidas"
        }
    }
    const menusEntrada=menusrestaurantes.map((datosmenu,index)=>
        
        <li key={index} className='listaMenuInfoRestaurante'><p>{datosmenu.nombreMenu}</p> <p>{tipo(datosmenu)}</p> valor: <p>{datosmenu.precio}</p></li>
    )
  return (
    <div className='constenedorListaMenuRestaurante'>
        <h2>Entrada</h2>
        <ul>
            {menusEntrada}
        </ul>
    </div>
  )
}

export default MenusInforRestaurantes
