import axios from 'axios'
import React, { useEffect, useState } from 'react'

const ListaIngredientesEdit = (datos) => {
    const [ingredientes,setIngredientes]=useState([])

    async function registros(){
        const respuesta=await axios.get("http://192.168.1.4:8080/ingredientes?NombreMenu="+datos.parametros.nombreMenu)
        setIngredientes(respuesta.data)
    }
    useEffect(()=>{
        registros();
    },[]);
    async function agregarIngrediente(evento){
        const repuesta=await axios.put("http://192.168.1.4:8080/ingredientes/create",{
            nombreMenu:''+datos.parametros.nombreMenu,
            nombreIngrediente:''+evento.target.nombreIngrediente.value,
            caloria:''+evento.target.CaloriaIngrediente.value
        })
        if (repuesta.data===1){
            registros()
        }else{
            alert("no se pudo añadir el ingrediente")
        }
        
    }

    async function quitarIngrediente(datosI){
        await axios.delete("http://192.168.1.4:8080/ingredientes/delete?idIngrediente="+datosI)
        registros()
    }
    const elementos=ingredientes.map((datosI,index)=>
        <li key={index} className='listaIngredientess'>
            <p>{datosI.nombreIngrediente} contiene {datosI.caloria} calorias</p>
            <BotonQuitar quitarIngrediente={quitarIngrediente} datosLista={datosI.idIngredientes} />        
        </li>
    )
    
  return (
    <div className='ingredientesEdit'>
        <from onSubmit={evento=>{evento.preventDefault();agregarIngrediente(evento);}}>
            <label>
                <input name='nombreIngrediente' type='text'/>
                <input name='CaloriaIngrediente' type='number' min={0} max={2000}/>
                <button type='submit'>agregar</button>
            </label>
        </from>
        <hr className='lineaIngredientes'/>
        <h2 className='tituloIngredientes'>Lista Ingredientes</h2>
        <hr className='lineaIngredientes'/>
        <ul className='lista'>
            {elementos}
        </ul>
    </div>
  )
}

export default ListaIngredientesEdit
