import axios from 'axios'
import React,{useEffect, useState} from 'react'
import { useParams } from 'react-router-dom'

const EditMenu = () => {
    const [Menu,setMenu]=useState(null)
    const url="http://192.168.1.4:8080/menus/get"
    const parametros=useParams()
    async function peticionMenu(){
        const respuesta=await axios.get(url+"?NombreMenu="+parametros.nombreMenu)
        setMenu(respuesta.data)
    }
    useEffect(()=>{
        peticionMenu();
      },[]);
  return (
    <div>{Menu.nombreMenu} : {Menu.tipoMenu} : {Menu.precio}</div>
  )
}

export default EditMenu
