import axios from 'axios'
import React from 'react'
import './estilosComponentes/listaelementomenu.css'
const ListaElementoMenu = (menu) => {
    function tipo(){
        if(menu.tipoMenu===1){
            return "Entrada"
        }else if(menu.tipoMenu===2){
            return "Plato Fuerte"
        }else if(menu.tipoMenu===3){
            return "Postres"
        }else{
            return "Bebidas"
        }
    }
    async function eliminar(){
        /*await axios.delete("http://192.168.1.4:8080//menus/delete",{
            tipoMenu:""+menu.tipoMenu,
            nombreMenu:""+menu.nombreMenu,
            precio:""+menu.precio
        })
        window.location.reload()*/
        alert("se elimino un dato")
    }
    function editar(){
        window.location.assign("http://192.168.1.4:3000/Menus/Edit/"+menu.nombreMenu)
    }
    function info(){
        window.location.assign("http://192.168.1.4:3000/Menus/Info/"+menu.nombreMenu)
    }
  return (
    <li key={menu.index} className='elementoRestaurante' >
        <div className='contenedorRestaurantes'>
            <div onClick={info} className='elementomenu'>
                <p>{menu.nombreMenu}</p>
                <p>{tipo()}</p>
                <p>{menu.precio}</p>
            </div>
            <div className='contenedorbotones'>
                <button className='editar' onClick={editar}></button>
                <button className='eliminar' onClick={eliminar}></button>
            </div>
        </div>
    </li>
  )
}

export default ListaElementoMenu
