import React,{useState,useEffect, useRef} from 'react'
import axios from 'axios'
import { useParams } from 'react-router-dom'

const EditMenu = () =>{
    const parametros=useParams()
    let nombre=useRef(null)
    const [editMenu,setEditMenu]=useState(Object)
    const url="http://192.168.1.4:8080/menus/get"
    async function peticiongetMenu(){
        const respuesta=await axios.get(url+"?NombreMenu="+parametros.nombreMenu)
        setEditMenu(respuesta.data)
    }
    useEffect(()=>{
        peticiongetMenu();
    },[]);
    
    return (
        <form>
            <div className='contenedorEdit'>
                <label>
                    Nombre: 
                    <input type='text' value={editMenu.nombreMenu} onChange={evento=>setEditMenu(evento.target.value)}/>
                </label>
            </div>
        </form>
    
    )
}

export default EditMenu
